mapper.select:
SELECT

FROM approval_process_point


    id = #{id}


    AND processId = #{processId}


    AND createdAt = #{createdAt}


    AND updatedAt = #{updatedAt}


    AND isEnable = #{isEnable}


    AND companyId = #{companyId}


    AND departmentId = #{departmentId}


    AND departmentName = #{departmentName}


    AND positionId = #{positionId}


    AND positionName = #{positionName}


    AND approverId = #{approverId}


    AND approverName = #{approverName}


    AND type = #{type}


    AND sort = #{sort}


    AND approvalType = #{approvalType}





ORDER BY sort ASC, createdAt ASC, updatedAt DESC

mapper.update:
UPDATE approval_process_point
processId = #{processId}, createdAt = #{createdAt}, updatedAt = #{updatedAt}, isEnable = #{isEnable}, companyId = #{companyId}, departmentId = #{departmentId}, departmentName = #{departmentName}, positionId = #{positionId}, positionName = #{positionName}, approverId = #{approverId}, approverName = #{approverName}, type = #{type}, sort = #{sort}, approvalType = #{approvalType},
WHERE id = #{id}
