CREATE TABLE `email_html_template` (  `id` varchar(36) NOT NULL COMMENT '主键',  `createdAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',  `updatedAt` timestamp NOT NULL ON UPDATE CURRENT_TIMESTAMP DEFAULT CURRENT_TIMESTAMP COMMENT '更新时间',  `isEnable` tinyint(1) NOT NULL DEFAULT 1 COMMENT '是否可用：0-不可用，1-可用',  `code` varchar(128) NULL COMMENT '模板类型',  `name` varchar(64) NULL COMMENT '模板名称',  `description` varchar(256) NULL COMMENT '模板描述',  `html` text NOT NULL COMMENT '模板内容',  `args` text NULL COMMENT '参数描述',  PRIMARY KEY (`id`)) COMMENT '网页邮件模板';CREATE TABLE `feedback` (  `id` varchar(36) NOT NULL COMMENT '主键',  `createdAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',  `updatedAt` timestamp NOT NULL ON UPDATE CURRENT_TIMESTAMP DEFAULT CURRENT_TIMESTAMP COMMENT '更新时间',  `isEnable` tinyint(1) NOT NULL DEFAULT 1 COMMENT '是否可用：0-不可用，1-可用',  `userId` varchar(36) NOT NULL COMMENT '反馈用户id',  `userCompanyId` varchar(36) NOT NULL COMMENT '反馈用户公司id',  `userCompanyName` varchar(36) NOT NULL COMMENT '反馈用户公司名称',  `userName` varchar(64) NULL COMMENT '用户名称',  `userEmail` varchar(128) NULL COMMENT '用户邮箱',  `userPhoneNumber` varchar(11) NULL COMMENT '用户手机号',  `clientType` int NOT NULL COMMENT '客户端类型',  `clientVersion` varchar(16) NOT NULL COMMENT '客户端版本',  `environmentJson` text NULL COMMENT '客户端环境JSON描述',  `content` text NULL COMMENT '反馈内容',  `isAccept` tinyint(1) NOT NULL DEFAULT 0 COMMENT '意见是否接受',  `isProcessed` tinyint(1) NOT NULL DEFAULT 0 COMMENT '意见是否处理',  `remark` varchar(256) NULL COMMENT '备注',  PRIMARY KEY (`id`)) COMMENT '使用反馈';CREATE TABLE `notice` (  `id` varchar(36) NOT NULL COMMENT '主键',  `createdAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',  `updatedAt` timestamp NOT NULL ON UPDATE CURRENT_TIMESTAMP DEFAULT CURRENT_TIMESTAMP COMMENT '更新时间',  `isEnable` tinyint(1) NOT NULL DEFAULT 1 COMMENT '是否可用：0-不可用，1-可用',  `releasedAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '发布时间',  `expiredAt` timestamp NULL COMMENT '过期时间',  `status` int NOT NULL default 0 COMMENT '状态: 0-待通知 1-通知中 2-已通知 3-部分通知(发生异常)',  `type` int NOT NULL COMMENT '类型: 0-系统公告 1-公司公告 2-部门公告 3-员工通知',  `lever` int NOT NULL default 0 COMMENT '级别: 0-一般通知 1-重要通知 2-紧急通知 3-加急通知',  `clientType` int NOT NULL default 0 COMMENT '站内信接收方客户端类型: 0-所有客户端 1-WEB端 4-小程序',  `method` int NOT NULL default 0 COMMENT '通知方式:0-站内通知 1-站内通知+邮件通知 2-站内通知+短信通知 3-站内通知+邮件通知+短信通知',  `title` varchar(128) NOT NULL COMMENT '标题',  `isShortNotice` tinyint(1) NOT NULL default 1 COMMENT '是否是简短公告(简短公告无内容id, 且不记录阅读记录)',  `contentId` varchar(36) NULL COMMENT '内容id',  `isNoticeCloseable` tinyint(1) NOT NULL default 1 COMMENT '是否允许用户关闭公告',  `senderDescription` varchar(128) NOT NULL default '系统中心' COMMENT '发布方描述',  `senderId` varchar(36) NULL COMMENT '发布方id',  `senderName` varchar(64) NULL COMMENT '发布方名称',  `senderCompanyId` varchar(36) NULL COMMENT '发布方公司id',  `senderCompanyName` varchar(128) NULL COMMENT '发布方公司名称',  `senderDepartmentId` varchar(36) NULL COMMENT '发布方部门id',  `senderDepartmentName` varchar(128) NULL COMMENT '发布方部门名称',  `receiverId` varchar(36) NULL COMMENT '接收方id',  `receiverName` varchar(128) NULL COMMENT '接收方名称',  `receiverCompanyId` varchar(36) NULL COMMENT '接收方公司id',  `receiverCompanyName` varchar(128) NULL COMMENT '接收方公司名称',  `receiverDepartmentId` varchar(36) NULL COMMENT '接收方部门id',  `receiverDepartmentName` varchar(128) NULL COMMENT '接收方部门名称',  `refType` varchar(36) NULL COMMENT '关联类型',  `refId` varchar(36) NULL COMMENT '关联类型id',  `remark` varchar(256) NULL COMMENT '备注',  PRIMARY KEY (`id`)) COMMENT '通知信息';CREATE TABLE `notice_record` (  `id` varchar(36) NOT NULL COMMENT '主键',  `createdAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',  `updatedAt` timestamp NOT NULL ON UPDATE CURRENT_TIMESTAMP DEFAULT CURRENT_TIMESTAMP COMMENT '更新时间',  `isEnable` tinyint(1) NOT NULL DEFAULT 1 COMMENT '是否可用：0-不可用，1-可用',  `userId` varchar(36) NOT NULL COMMENT '用户id',  `userName` varchar(64) NOT NULL COMMENT '用户名称',  `noticeId` varchar(36) NOT NULL COMMENT '公告id',  `isViewed` tinyint(1) NOT NULL DEFAULT 0 COMMENT '站内信是否已读：0-未读，1-已读',  PRIMARY KEY (`id`)) COMMENT '通知发送记录';CREATE TABLE `sms_send_template` (  `id` varchar(36) NOT NULL COMMENT '主键',  `createdAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',  `updatedAt` timestamp NOT NULL ON UPDATE CURRENT_TIMESTAMP DEFAULT CURRENT_TIMESTAMP COMMENT '更新时间',  `isEnable` tinyint(1) NOT NULL DEFAULT 1 COMMENT '是否可用：0-不可用，1-可用',  `creatorId` varchar(36) NOT NULL COMMENT '创建人id',  `code` varchar(36) NULL COMMENT '系统内部编码',  `name` varchar(64) NULL COMMENT '模板名称',  `type` int NOT NULL COMMENT '模板类型: 0-验证码 1-短信通知 2-推广短信',  `templateCode` varchar(128) NULL COMMENT '阿里云的模板编号',  `content` varchar(512) NOT NULL COMMENT '模板内容',  `status` int NOT NULL DEFAULT 0 COMMENT '状态：0-审核中 1-已通过 2-未通过',  `remark` varchar(512) NULL COMMENT '备注',  PRIMARY KEY (`id`)) COMMENT '短信发送模板';CREATE TABLE `sms_send_record` (  `id` varchar(36) NOT NULL COMMENT '主键',  `createdAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',  `updatedAt` timestamp NOT NULL ON UPDATE CURRENT_TIMESTAMP DEFAULT CURRENT_TIMESTAMP COMMENT '更新时间',  `isEnable` tinyint(1) NOT NULL DEFAULT 1 COMMENT '是否可用：0-不可用，1-可用',  `userId` varchar(36) NULL COMMENT '用户id',  `companyId` varchar(36) NULL COMMENT '公司id',  `departmentId` varchar(36) NULL COMMENT '部门id',  `noticeId` varchar(36) NULL COMMENT '公告id',  `phoneNumber` varchar(16) NOT NULL COMMENT '手机号',  `templateId` varchar(36) NOT NULL COMMENT '短信模板id',  `signature` varchar(36) NOT NULL COMMENT '短信签名',  `paramsJson` varchar(512) NULL COMMENT '短信参数',  `size` int NOT NULL default 1 COMMENT '短信长度: 70个汉字/140个英文字符 算1条',  `sendAt` timestamp NULL COMMENT '发送时间',  `receivedAt` timestamp NULL COMMENT '接收时间',  `bizId` varchar(128) NULL COMMENT '发送序列号',  `outId` varchar(128) NULL COMMENT '用户序列号',  `reportCode` varchar(128) NULL COMMENT '状态报告编码',  `reportMessage` varchar(512) NULL COMMENT '状态报告说明',  `status` int NOT NULL DEFAULT 0 COMMENT '状态：0-等待回执 1-发送成功 2-发送失败',  PRIMARY KEY (`id`)) COMMENT '短信发送记录';