CREATE TABLE `rich_text` (  `id` varchar(36) NOT NULL COMMENT '主键',  `createdAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',  `updatedAt` timestamp NOT NULL ON UPDATE CURRENT_TIMESTAMP DEFAULT CURRENT_TIMESTAMP COMMENT '更新时间',  `isEnable` tinyint(1) NOT NULL DEFAULT 1 COMMENT '是否可用：0-不可用，1-可用',  `raw` MEDIUMTEXT NULL COMMENT '富文本编辑用raw字符串',  `html` MEDIUMTEXT NULL COMMENT '富文本展示用html字符串',  `creatorId` varchar(36) NOT NULL COMMENT '创建人id',  `outlook` varchar(128) NULL COMMENT '富文本内容类型简述',  PRIMARY KEY (`id`)) COMMENT '富文本存储';CREATE TABLE `upload_record` (  `id` varchar(36) NOT NULL COMMENT '主键',  `createdAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',  `updatedAt` timestamp NOT NULL ON UPDATE CURRENT_TIMESTAMP DEFAULT CURRENT_TIMESTAMP COMMENT '更新时间',  `isEnable` tinyint(1) NOT NULL DEFAULT 1 COMMENT '是否可用：0-不可用，1-可用',  `name` varchar(256) NULL COMMENT '文件名称',  `size` int NULL COMMENT '文件大小',  `suffix` varchar(36) NULL COMMENT '文件后缀',  `mimeType` varchar(128) NULL COMMENT 'MIME类型',  `key` varchar(128) NULL COMMENT '文件key',  `hash` varchar(128) NULL COMMENT '文件hash',  `bucket` varchar(128) NULL COMMENT '所在空间',  `creatorId` varchar(36) NOT NULL COMMENT '创建人id',  PRIMARY KEY (`id`)) COMMENT '文件上传记录';CREATE TABLE `signature_record` (  `id` varchar(36) NOT NULL COMMENT '主键',  `createdAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',  `updatedAt` timestamp NOT NULL ON UPDATE CURRENT_TIMESTAMP DEFAULT CURRENT_TIMESTAMP COMMENT '更新时间',  `isEnable` tinyint(1) NOT NULL DEFAULT 1 COMMENT '是否可用：0-不可用，1-可用',  `userId` varchar(36) NOT NULL COMMENT '用户id',  `userName` varchar(64) NOT NULL COMMENT '用户名称',  `signature` MEDIUMTEXT NOT NULL COMMENT '签字文件(DataUrl形式的PNG图片)',  PRIMARY KEY (`id`)) COMMENT '签字';CREATE TABLE `statistics_record` (  `id` varchar(36) NOT NULL COMMENT '主键',  `createdAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',  `updatedAt` timestamp NOT NULL ON UPDATE CURRENT_TIMESTAMP DEFAULT CURRENT_TIMESTAMP COMMENT '更新时间',  `isEnable` tinyint(1) NOT NULL DEFAULT 1 COMMENT '是否可用：0-不可用，1-可用',  `startAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '统计开始时间',  `endAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '统计结束时间',  `service` varchar(64) NOT NULL COMMENT '统计所在服务',  `type` varchar(64) NOT NULL COMMENT '统计数据类型',  `refId` varchar(36) NOT NULL COMMENT '统计数据关联id',  `dataJson` text NOT NULL COMMENT '统计数据JSON格式存储',  `remark` varchar(256) NOT NULL COMMENT '备注',  PRIMARY KEY (`id`)) COMMENT '统计记录';