mapper.select:
SELECT

FROM re_role_permission


    id = #{id}


    AND createdAt = #{createdAt}


    AND updatedAt = #{updatedAt}


    AND isEnable = #{isEnable}


    AND roleId = #{roleId}


    AND permissionId = #{permissionId}





ORDER BY createdAt ASC, updatedAt DESC

mapper.update:
UPDATE re_role_permission
createdAt = #{createdAt}, updatedAt = #{updatedAt}, isEnable = #{isEnable}, roleId = #{roleId}, permissionId = #{permissionId},
WHERE id = #{id}

mapper.select:
SELECT

FROM re_role_permission
WHERE re_role_permission.roleId = #{roleId}
AND re_role_permission.permissionId = #{permissionId}
