mapper.select:
SELECT

FROM supplier_register


      id = #{id}


      AND createdAt = #{createdAt}


      AND updatedAt = #{updatedAt}


      AND isEnable = #{isEnable}


      AND companyName like CONCAT('%',#{companyName},'%')


      AND unifiedSocialCreditCode = #{unifiedSocialCreditCode}


      AND supplierType = #{supplierType}


      AND supplierLevel = #{supplierLevel}


      AND headOfSalesName like CONCAT('%',#{headOfSalesName},'%')


      AND headOfSalesIdCard = #{headOfSalesIdCard}


      AND headOfSalesPhoneNumber = #{headOfSalesPhoneNumber}


      AND headOfSalesEmail = #{headOfSalesEmail}


      AND headOfSalesPassword = #{headOfSalesPassword}


      AND idCardPhotocopy = #{idCardPhotocopy}


      AND activeCode = #{activeCode}


      AND remark = #{remark}


      AND approverId = #{approverId}


      AND result = #{result}


      AND status = #{status}





ORDER BY createdAt ASC, updatedAt DESC

mapper.update:
UPDATE supplier_register
createdAt = #{createdAt}, updatedAt = #{updatedAt}, isEnable = #{isEnable}, companyName = #{companyName}, unifiedSocialCreditCode = #{unifiedSocialCreditCode}, supplierType = #{supplierType}, supplierLevel = #{supplierLevel}, headOfSalesName = #{headOfSalesName}, headOfSalesIdCard = #{headOfSalesIdCard}, headOfSalesPhoneNumber = #{headOfSalesPhoneNumber}, headOfSalesEmail = #{headOfSalesEmail}, headOfSalesPassword = #{headOfSalesPassword}, idCardPhotocopy = #{idCardPhotocopy}, activeCode = #{activeCode}, remark = #{remark}, approverId = #{approverId}, result = #{result}, status = #{status},
WHERE id = #{id}
