mapper.select:
SELECT

FROM equipment_permission


      id = #{id}


      AND createdAt = #{createdAt}


      AND updatedAt = #{updatedAt}


      AND isEnable = #{isEnable}


      AND equipmentId = #{equipmentId}


      AND type = #{type}


      AND companyId = #{companyId}


      AND companyName = #{companyName}


      AND departmentId = #{departmentId}


      AND departmentName = #{departmentName}


      AND employeeId = #{employeeId}


      AND employeeName = #{employeeName}


      AND permission = #{permission}


      AND obtainAt = #{obtainAt}


      AND obtainReason = #{obtainReason}


      AND loseAt = #{loseAt}


      AND loseReason = #{loseReason}





ORDER BY createdAt ASC, updatedAt DESC

mapper.update:
UPDATE equipment_permission
createdAt = #{createdAt}, updatedAt = #{updatedAt}, isEnable = #{isEnable}, equipmentId = #{equipmentId}, type = #{type}, companyId = #{companyId}, companyName = #{companyName}, departmentId = #{departmentId}, departmentName = #{departmentName}, employeeId = #{employeeId}, employeeName = #{employeeName}, permission = #{permission}, obtainAt = #{obtainAt}, obtainReason = #{obtainReason}, loseAt = #{loseAt}, loseReason = #{loseReason},
WHERE id = #{id}

mapper.select:
select distinct e.* from equipment e
join equipment_permission ep on ep.isEnable = true
  and ep.equipmentId = e.id
  and ep.permission = #{permissionCode}
  and ep.type = #{type}

    and ep.companyId = #{typeId}


    and ep.departmentId = #{typeId}


    and ep.employeeId = #{typeId}

where e.isEnable = true
order by e.createdAt asc

select distinct eprod.*
from equipment_product eprod
join equipment e on e.equipmentProductId = eprod.id and e.isEnable = true
join equipment_permission ep on ep.equipmentId = e.id and ep.isEnable = true
  and ep.permission = #{permissionCode}
  and ep.type = #{type}

    and ep.companyId = #{typeId}


    and ep.departmentId = #{typeId}


    and ep.employeeId = #{typeId}

where eprod.isEnable = true
order by eprod.createdAt asc

select distinct eti.*, etd.dSort, etd.uSort, etd.description, etd.`usage`
from equipment_type_item eti
left join re_equipment_type_item_description retid on retid.itemId = eti.id and retid.isEnable = true
left join equipment_type_description etd on etd.id = retid.descriptionId and etd.isEnable = true
join equipment e on e.equipmentTypeId = eti.id and e.isEnable = true
join equipment_permission ep on ep.equipmentId = e.id and ep.isEnable = true
  and ep.permission = #{permissionCode}
  and ep.type = #{type}

    and ep.companyId = #{typeId}


    and ep.departmentId = #{typeId}


    and ep.employeeId = #{typeId}

where eti.isEnable = true
order by eti.id asc

select count(distinct e.id)
from equipment e
join equipment_permission ep on ep.isEnable = true
and ep.equipmentId = e.id
and ep.permission = #{permissionCode}
and ep.type = #{type}

  and ep.companyId = #{typeId}


  and ep.departmentId = #{typeId}


  and ep.employeeId = #{typeId}

where e.isEnable = true

select count(distinct eprod.id)
from equipment_product eprod
join equipment e on e.equipmentProductId = eprod.id and e.isEnable = true
join equipment_permission ep on ep.equipmentId = e.id and ep.isEnable = true
and ep.permission = #{permissionCode}
and ep.type = #{type}

  and ep.companyId = #{typeId}


  and ep.departmentId = #{typeId}


  and ep.employeeId = #{typeId}

where eprod.isEnable = true

select count(distinct eti.id)
from equipment_type_item eti
join equipment e on e.equipmentTypeId = eti.id and e.isEnable = true
join equipment_permission ep on ep.equipmentId = e.id and ep.isEnable = true
and ep.permission = #{permissionCode}
and ep.type = #{type}

  and ep.companyId = #{typeId}


  and ep.departmentId = #{typeId}


  and ep.employeeId = #{typeId}

where eti.isEnable = true
