mapper.select:
SELECT
  *
FROM equipment_product


    id = #{id}


    AND createdAt = #{createdAt}


    AND updatedAt = #{updatedAt}


    AND isEnable = #{isEnable}


    AND commodityName like CONCAT('%',#{commodityName},'%')


    AND equipmentTypeId = #{equipmentTypeId}


    AND equipmentTypeName = #{equipmentTypeName}


    AND managementLever = #{managementLever}


    AND specification = #{specification}


    AND modelNumber = #{modelNumber}


    AND manufacturerName = #{manufacturerName}


    AND gtin = #{gtin}


    AND brand = #{brand}


    AND isSterileProvide = #{isSterileProvide}


    AND isImportProduct = #{isImportProduct}


    AND isLargeEquipment = #{isLargeEquipment}


    AND expectedUsefulLife = #{expectedUsefulLife}


    AND sumWorkloadExpectancy = #{sumWorkloadExpectancy}


    AND technicalIndicatorJson = #{technicalIndicatorJson}


    AND functionsJson = #{functionsJson}


    AND mdRegistrationCertificateId = #{mdRegistrationCertificateId}


    AND mdProductionCertificateId = #{mdProductionCertificateId}


    AND otherCertificatePhotoCopy = #{otherCertificatePhotoCopy}


    AND sparePartsListJson = #{sparePartsListJson}


    AND accessoryListJson = #{accessoryListJson}


    AND mainComponentsJson = #{mainComponentsJson}


    AND deliveryComponentsJson = #{deliveryComponentsJson}


    AND electronicDocumentStorage = #{electronicDocumentStorage}


    AND installType = #{installType}


    AND installationInfoId = #{installationInfoId}


    AND environmentInfoId = #{environmentInfoId}


    AND maintenanceInfoId = #{maintenanceInfoId}


    AND repairInfo = #{repairInfo}


    AND maintenancePassword = #{maintenancePassword}


    AND faultCodeMeterJson = #{faultCodeMeterJson}


    AND descriptionId = #{descriptionId}


    AND instructionId = #{instructionId}


    AND warningInfoId = #{warningInfoId}


    AND storageInfoId = #{storageInfoId}


    AND wasteTreatmentInfoId = #{wasteTreatmentInfoId}


    AND wasteClassification = #{wasteClassification}


    AND otherInfoId = #{otherInfoId}


    AND companyId = #{companyId}


    AND status = #{status}


    AND currentId = #{currentId}


    AND applicationId = #{applicationId}


    AND photos = #{photos}


    AND isInspection = #{isInspection}


    AND supplierCompanyId = #{supplierCompanyId}


    AND supplierCompanyName = #{supplierCompanyName}





ORDER BY createdAt ASC, updatedAt DESC

mapper.update:
UPDATE equipment_product
createdAt = #{createdAt}, updatedAt = #{updatedAt}, isEnable = #{isEnable}, commodityName = #{commodityName}, equipmentTypeId = #{equipmentTypeId}, equipmentTypeName = #{equipmentTypeName}, managementLever = #{managementLever}, specification = #{specification}, modelNumber = #{modelNumber}, manufacturerName = #{manufacturerName}, gtin = #{gtin}, brand = #{brand}, isSterileProvide = #{isSterileProvide}, isImportProduct = #{isImportProduct}, isLargeEquipment = #{isLargeEquipment}, expectedUsefulLife = #{expectedUsefulLife}, sumWorkloadExpectancy = #{sumWorkloadExpectancy}, technicalIndicatorJson = #{technicalIndicatorJson}, functionsJson = #{functionsJson}, mdRegistrationCertificateId = #{mdRegistrationCertificateId}, mdProductionCertificateId = #{mdProductionCertificateId}, otherCertificatePhotoCopy = #{otherCertificatePhotoCopy}, sparePartsListJson = #{sparePartsListJson}, accessoryListJson = #{accessoryListJson}, mainComponentsJson = #{mainComponentsJson}, deliveryComponentsJson = #{deliveryComponentsJson}, electronicDocumentStorage = #{electronicDocumentStorage}, installType = #{installType}, installationInfoId = #{installationInfoId}, environmentInfoId = #{environmentInfoId}, maintenanceInfoId = #{maintenanceInfoId}, repairInfo = #{repairInfo}, maintenancePassword = #{maintenancePassword}, faultCodeMeterJson = #{faultCodeMeterJson}, descriptionId = #{descriptionId}, instructionId = #{instructionId}, warningInfoId = #{warningInfoId}, storageInfoId = #{storageInfoId}, wasteTreatmentInfoId = #{wasteTreatmentInfoId}, wasteClassification = #{wasteClassification}, otherInfoId = #{otherInfoId}, companyId = #{companyId}, status = #{status}, currentId = #{currentId}, applicationId = #{applicationId}, photos = #{photos}, isInspection = #{isInspection}, supplierCompanyId = #{supplierCompanyId}, supplierCompanyName = #{supplierCompanyName},
WHERE id = #{id}

mapper.select:
SELECT ep.id, ep.technicalIndicatorJson, ep.functionsJson
FROM equipment_product ep
WHERE ep.id=#{id}
