mapper.select:
SELECT

FROM equipment_type_item


      id = #{id}


      AND createdAt = #{createdAt}


      AND updatedAt = #{updatedAt}


      AND isEnable = #{isEnable}


      AND indexId = #{indexId}


      AND indexName = #{indexName}


      AND firstId = #{firstId}


      AND firstName = #{firstName}


      AND secondId = #{secondId}


      AND secondName = #{secondName}


      AND name = #{name}


      AND managementCategory = #{managementCategory}





ORDER BY createdAt ASC, updatedAt DESC

mapper.update:
UPDATE equipment_type_item
createdAt = #{createdAt}, updatedAt = #{updatedAt}, isEnable = #{isEnable}, indexId = #{indexId}, indexName = #{indexName}, firstId = #{firstId}, firstName = #{firstName}, secondId = #{secondId}, secondName = #{secondName}, name = #{name}, managementCategory = #{managementCategory},
WHERE id = #{id}
