mapper.select:
SELECT

FROM maintenance_cost_item


      id = #{id}


      AND createdAt = #{createdAt}


      AND updatedAt = #{updatedAt}


      AND isEnable = #{isEnable}


      AND equipmentId = #{equipmentId}


      AND taskId = #{taskId}


      AND taskAssignmentId = #{taskAssignmentId}


      AND engineerId = #{engineerId}


      AND engineerName = #{engineerName}


      AND type = #{type}


      AND name = #{name}


      AND specification = #{specification}


      AND description = #{description}


      AND price = #{price}


      AND count = #{count}


      AND sumPrice = #{sumPrice}





ORDER BY createdAt ASC, updatedAt DESC

mapper.update:
UPDATE maintenance_cost_item
createdAt = #{createdAt}, updatedAt = #{updatedAt}, isEnable = #{isEnable}, equipmentId = #{equipmentId}, taskId = #{taskId}, taskAssignmentId = #{taskAssignmentId}, engineerId = #{engineerId}, engineerName = #{engineerName}, type = #{type}, name = #{name}, specification = #{specification}, description = #{description}, price = #{price}, count = #{count}, sumPrice = #{sumPrice},
WHERE id = #{id}

mapper.select:
select mci.createdAt, mci.type, mci.sumPrice
from maintenance_cost_item mci

  join engineer e on e.serveCompanyId = #{companyId} and mci.engineerId = e.id

where mci.isEnable = true

  and mci.createdAt >= #{startAt}


  and mci.equipmentId = #{equipmentId}
