mapper.select:
SELECT

FROM maintenance_method


    id = #{id}


    AND createdAt = #{createdAt}


    AND updatedAt = #{updatedAt}


    AND isEnable = #{isEnable}


    AND serveType = #{serveType}


    AND serveId = #{serveId}


    AND serveName = #{serveName}


    AND minInterval = #{minInterval}


    AND score = #{score}


    AND name like CONCAT('%',#{name},'%')


    AND description = #{description}


    AND stepInfoId = #{stepInfoId}


    AND warningInfoId = #{warningInfoId}


    AND dataTypeJson = #{dataTypeJson}


    AND companyId = #{companyId}





ORDER BY createdAt ASC, updatedAt DESC

mapper.update:
UPDATE maintenance_method
createdAt = #{createdAt}, updatedAt = #{updatedAt}, isEnable = #{isEnable}, serveType = #{serveType}, serveId = #{serveId}, serveName = #{serveName}, minInterval = #{minInterval}, score = #{score}, name = #{name}, description = #{description}, stepInfoId = #{stepInfoId}, warningInfoId = #{warningInfoId}, dataTypeJson = #{dataTypeJson}, companyId = #{companyId},
WHERE id = #{id}
