mapper.select:
SELECT
  mr.*
FROM maintenance_record mr

  join engineer e on e.id = mr.engineerId and e.isEnable = true and e.userId = #{userId}



    mr.id = #{id}


    AND mr.createdAt = #{createdAt}


    AND mr.updatedAt = #{updatedAt}


    AND mr.isEnable = #{isEnable}


    AND mr.equipmentId = #{equipmentId}


    AND mr.equipmentDescription = #{equipmentDescription}


    AND mr.equipmentPosition = #{equipmentPosition}


    AND mr.taskId = #{taskId}


    AND mr.engineerId = #{engineerId}


    AND mr.engineerName = #{engineerName}


    AND mr.companyId = #{companyId}


    AND mr.remark = #{remark}



    and mr.createdAt >= #{createdAtGreaterThan}


    and #{createdAtLessThan} > mr.createdAt



ORDER BY mr.createdAt DESC

mapper.update:
UPDATE maintenance_record
createdAt = #{createdAt}, updatedAt = #{updatedAt}, isEnable = #{isEnable}, equipmentId = #{equipmentId}, equipmentDescription = #{equipmentDescription}, equipmentPosition = #{equipmentPosition}, taskId = #{taskId}, engineerId = #{engineerId}, engineerName = #{engineerName}, companyId = #{companyId}, remark = #{remark},
WHERE id = #{id}
