mapper.select:
SELECT

FROM repair_dispatch_inside


      id = #{id}


      AND createdAt = #{createdAt}


      AND updatedAt = #{updatedAt}


      AND isEnable = #{isEnable}


      AND requestId = #{requestId}


      AND engineerId = #{engineerId}


      AND engineerName = #{engineerName}


      AND status = #{status}


      AND receivedAt = #{receivedAt}


      AND workTimeJson = #{workTimeJson}


      AND isReDispatch = #{isReDispatch}


      AND reDispatchDescription = #{reDispatchDescription}


      AND reDispatchReason = #{reDispatchReason}





ORDER BY createdAt ASC, updatedAt DESC

mapper.update:
UPDATE repair_dispatch_inside
createdAt = #{createdAt}, updatedAt = #{updatedAt}, isEnable = #{isEnable}, requestId = #{requestId}, engineerId = #{engineerId}, engineerName = #{engineerName}, status = #{status}, receivedAt = #{receivedAt}, workTimeJson = #{workTimeJson}, isReDispatch = #{isReDispatch}, reDispatchDescription = #{reDispatchDescription}, reDispatchReason = #{reDispatchReason},
WHERE id = #{id}

mapper.select:
SELECT
  rr.*,
  rdi.id as dispatchId,
  rdi.engineerId as engineerId,
  rdi.engineerName as engineerName,
  rdi.status as dispatchStatus,
  rdi.receivedAt as receivedAt,
  rdi.workTimeJson as workTimeJson,
  rdi.isReDispatch as isReDispatch,
  rdi.reDispatchDescription as reDispatchDescription,
  rdi.reDispatchReason as reDispatchReason
FROM repair_dispatch_inside rdi

  join engineer e on e.id = rdi.engineerId and e.isEnable = true and e.userId = #{engineerUserId}

join repair_request rr on rr.id = rdi.requestId and rr.isEnable = true

    and rr.equipmentId = #{equipmentId}


    and rr.equipmentTypeId = #{equipmentTypeId}


    and rr.reporterDepartmentId = #{reporterDepartmentId}


    and rr.lever = #{requestLever}


    and rr.status = #{requestStatus}


    and rr.createdAt >= #{requestCreatedAtGreaterThan}


    and #{requestCreatedAtLessThan} > rr.createdAt




    rdi.id = #{id}


    AND rdi.createdAt = #{createdAt}


    AND #{createdAtEnd} > rdi.createdAt


    AND rdi.createdAt >= #{createdAtStart}


    AND rdi.updatedAt = #{updatedAt}


    AND rdi.isEnable = #{isEnable}


    AND rdi.requestId = #{requestId}


    AND rdi.engineerId = #{engineerId}


    AND rdi.engineerName = #{engineerName}


    AND rdi.status = #{status}


    AND rdi.receivedAt = #{receivedAt}


    AND rdi.isReDispatch = #{isReDispatch}


ORDER BY rr.sort DESC, rdi.createdAt DESC

select createdAt, receivedAt, status
from repair_dispatch_inside where isEnable = true

  AND engineerId = #{engineerId}


  AND createdAt >= #{startAt}
