mapper.select:
SELECT

FROM repair_dispatch_outside


    id = #{id}


    AND createdAt = #{createdAt}


    AND updatedAt = #{updatedAt}


    AND isEnable = #{isEnable}


    AND requestId = #{requestId}


    AND contractId = #{contractId}


    AND companyId = #{companyId}


    AND companyName = #{companyName}


    AND workTimeJson = #{workTimeJson}


    AND method = #{method}


    AND mailStatus = #{mailStatus}


    AND mailSendInfo = #{mailSendInfo}


    AND mailReceiveInfo = #{mailReceiveInfo}


    AND isArrive = #{isArrive}


    AND isFinish = #{isFinish}


    AND isBreach = #{isBreach}


    AND result = #{result}


    AND remark = #{remark}





ORDER BY createdAt ASC, updatedAt DESC

mapper.update:
UPDATE repair_dispatch_outside
createdAt = #{createdAt}, updatedAt = #{updatedAt}, isEnable = #{isEnable}, requestId = #{requestId}, contractId = #{contractId}, companyId = #{companyId}, companyName = #{companyName}, workTimeJson = #{workTimeJson}, method = #{method}, mailStatus = #{mailStatus}, mailSendInfo = #{mailSendInfo}, mailReceiveInfo = #{mailReceiveInfo}, isArrive = #{isArrive}, isFinish = #{isFinish}, isBreach = #{isBreach}, result = #{result}, remark = #{remark},
WHERE id = #{id}
