mapper.select:
SELECT

FROM repair_evaluation


      id = #{id}


      AND createdAt = #{createdAt}


      AND updatedAt = #{updatedAt}


      AND isEnable = #{isEnable}


      AND requestId = #{requestId}


      AND engineerId = #{engineerId}


      AND engineerName = #{engineerName}


      AND punctual = #{punctual}


      AND attitude = #{attitude}


      AND service = #{service}


      AND comment = #{comment}


      AND appraiserid = #{appraiserid}


      AND appraiserName = #{appraiserName}


      AND appraiserDepartmentId = #{appraiserDepartmentId}


      AND appraiserDepartmentName = #{appraiserDepartmentName}





ORDER BY createdAt ASC, updatedAt DESC

mapper.update:
UPDATE repair_evaluation
createdAt = #{createdAt}, updatedAt = #{updatedAt}, isEnable = #{isEnable}, requestId = #{requestId}, engineerId = #{engineerId}, engineerName = #{engineerName}, punctual = #{punctual}, attitude = #{attitude}, service = #{service}, comment = #{comment}, appraiserid = #{appraiserid}, appraiserName = #{appraiserName}, appraiserDepartmentId = #{appraiserDepartmentId}, appraiserDepartmentName = #{appraiserDepartmentName},
WHERE id = #{id}
