mapper.select:
SELECT

FROM repair_help_quotation_approval


      id = #{id}


      AND createdAt = #{createdAt}


      AND updatedAt = #{updatedAt}


      AND isEnable = #{isEnable}


      AND requestId = #{requestId}


      AND dispatchId = #{dispatchId}


      AND engineerId = #{engineerId}


      AND engineerName = #{engineerName}


      AND outsideDispatchId = #{outsideDispatchId}


      AND outsideEngineerId = #{outsideEngineerId}


      AND outsideEngineerName = #{outsideEngineerName}


      AND signatureId = #{signatureId}


      AND price = #{price}


      AND description = #{description}


      AND applyId = #{applyId}


      AND status = #{status}





ORDER BY createdAt ASC, updatedAt DESC

mapper.update:
UPDATE repair_help_quotation_approval
createdAt = #{createdAt}, updatedAt = #{updatedAt}, isEnable = #{isEnable}, requestId = #{requestId}, dispatchId = #{dispatchId}, engineerId = #{engineerId}, engineerName = #{engineerName}, outsideDispatchId = #{outsideDispatchId}, outsideEngineerId = #{outsideEngineerId}, outsideEngineerName = #{outsideEngineerName}, signatureId = #{signatureId}, price = #{price}, description = #{description}, applyId = #{applyId}, status = #{status},
WHERE id = #{id}
