mapper.select:
SELECT

FROM repair_replacement


      id = #{id}


      AND createdAt = #{createdAt}


      AND updatedAt = #{updatedAt}


      AND isEnable = #{isEnable}


      AND requestId = #{requestId}


      AND dispatchId = #{dispatchId}


      AND engineerId = #{engineerId}


      AND engineerName = #{engineerName}


      AND outsideDispatchId = #{outsideDispatchId}


      AND outsideEngineerId = #{outsideEngineerId}


      AND outsideEngineerName = #{outsideEngineerName}


      AND type = #{type}


      AND status = #{status}


      AND name = #{name}


      AND specification = #{specification}


      AND description = #{description}


      AND price = #{price}


      AND count = #{count}


      AND sumPrice = #{sumPrice}


      AND applyId = #{applyId}


      AND procurementId = #{procurementId}


      AND procurementDetailId = #{procurementDetailId}


      AND sparePartOutRepairApplyId = #{sparePartOutRepairApplyId}


      AND sparePartOutBillId = #{sparePartOutBillId}


      AND sparePartOutItemId = #{sparePartOutItemId}


      AND signatureId = #{signatureId}





ORDER BY createdAt ASC, updatedAt DESC

mapper.update:
UPDATE repair_replacement
createdAt = #{createdAt}, updatedAt = #{updatedAt}, isEnable = #{isEnable}, requestId = #{requestId}, dispatchId = #{dispatchId}, engineerId = #{engineerId}, engineerName = #{engineerName}, outsideDispatchId = #{outsideDispatchId}, outsideEngineerId = #{outsideEngineerId}, outsideEngineerName = #{outsideEngineerName}, type = #{type}, status = #{status}, name = #{name}, specification = #{specification}, description = #{description}, price = #{price}, count = #{count}, sumPrice = #{sumPrice}, applyId = #{applyId}, procurementId = #{procurementId}, procurementDetailId = #{procurementDetailId}, sparePartOutRepairApplyId = #{sparePartOutRepairApplyId}, sparePartOutBillId = #{sparePartOutBillId}, sparePartOutItemId = #{sparePartOutItemId}, signatureId = #{signatureId},
WHERE id = #{id}
