mapper.select:
SELECT

FROM spare_part_in_bill


      id = #{id}


      AND createdAt = #{createdAt}


      AND updatedAt = #{updatedAt}


      AND isEnable = #{isEnable}


      AND outlook = #{outlook}


      AND origin = #{origin}


      AND applyId = #{applyId}


      AND status = #{status}


      AND senderId = #{senderId}


      AND senderName = #{senderName}


      AND senderDepartmentId = #{senderDepartmentId}


      AND senderDepartmentName = #{senderDepartmentName}


      AND senderCompanyId = #{senderCompanyId}


      AND senderCompanyName = #{senderCompanyName}


      AND sumCount = #{sumCount}


      AND sumPrice = #{sumPrice}


      AND sumPriceChinese = #{sumPriceChinese}


      AND approverId = #{approverId}


      AND approverName = #{approverName}


      AND approverSignatureId = #{approverSignatureId}


      AND approverDepartmentId = #{approverDepartmentId}


      AND approverDepartmentName = #{approverDepartmentName}


      AND approverCompanyId = #{approverCompanyId}


      AND approverCompanyName = #{approverCompanyName}


      AND remark = #{remark}





ORDER BY createdAt ASC, updatedAt DESC

mapper.update:
UPDATE spare_part_in_bill
createdAt = #{createdAt}, updatedAt = #{updatedAt}, isEnable = #{isEnable}, outlook = #{outlook}, origin = #{origin}, applyId = #{applyId}, status = #{status}, senderId = #{senderId}, senderName = #{senderName}, senderDepartmentId = #{senderDepartmentId}, senderDepartmentName = #{senderDepartmentName}, senderCompanyId = #{senderCompanyId}, senderCompanyName = #{senderCompanyName}, sumCount = #{sumCount}, sumPrice = #{sumPrice}, sumPriceChinese = #{sumPriceChinese}, approverId = #{approverId}, approverName = #{approverName}, approverSignatureId = #{approverSignatureId}, approverDepartmentId = #{approverDepartmentId}, approverDepartmentName = #{approverDepartmentName}, approverCompanyId = #{approverCompanyId}, approverCompanyName = #{approverCompanyName}, remark = #{remark},
WHERE id = #{id}
