mapper.select:
SELECT

FROM spare_part_in_item


      id = #{id}


      AND createdAt = #{createdAt}


      AND updatedAt = #{updatedAt}


      AND isEnable = #{isEnable}


      AND priceType = #{priceType}


      AND name = #{name}


      AND specification = #{specification}


      AND unit = #{unit}


      AND price = #{price}


      AND count = #{count}


      AND sumPrice = #{sumPrice}


      AND sort = #{sort}


      AND status = #{status}


      AND sparePartInBillId = #{sparePartInBillId}


      AND repairReplacementId = #{repairReplacementId}


      AND procurementId = #{procurementId}


      AND procurementDetailId = #{procurementDetailId}


      AND manufacturerName = #{manufacturerName}


      AND model = #{model}


      AND sparePartId = #{sparePartId}


      AND remark = #{remark}





ORDER BY createdAt ASC, updatedAt DESC

mapper.update:
UPDATE spare_part_in_item
createdAt = #{createdAt}, updatedAt = #{updatedAt}, isEnable = #{isEnable}, priceType = #{priceType}, name = #{name}, specification = #{specification}, unit = #{unit}, price = #{price}, count = #{count}, sumPrice = #{sumPrice}, sort = #{sort}, status = #{status}, sparePartInBillId = #{sparePartInBillId}, repairReplacementId = #{repairReplacementId}, procurementId = #{procurementId}, procurementDetailId = #{procurementDetailId}, manufacturerName = #{manufacturerName}, model = #{model}, sparePartId = #{sparePartId}, remark = #{remark},
WHERE id = #{id}
