mapper.select:
SELECT

FROM spare_part_type


      id = #{id}


      AND createdAt = #{createdAt}


      AND updatedAt = #{updatedAt}


      AND isEnable = #{isEnable}


      AND name = #{name}


      AND specification = #{specification}


      AND description = #{description}


      AND priceType = #{priceType}


      AND priceAvg = #{priceAvg}


      AND unit = #{unit}


      AND picture = #{picture}


      AND document = #{document}


      AND minCount = #{minCount}


      AND maxCount = #{maxCount}


      AND inTransitInCount = #{inTransitInCount}


      AND inTransitOutCount = #{inTransitOutCount}


      AND retailPurchases = #{retailPurchases}


      AND consumption = #{consumption}





ORDER BY createdAt ASC, updatedAt DESC

mapper.update:
UPDATE spare_part_type
createdAt = #{createdAt}, updatedAt = #{updatedAt}, isEnable = #{isEnable}, name = #{name}, specification = #{specification}, description = #{description}, priceType = #{priceType}, priceAvg = #{priceAvg}, unit = #{unit}, picture = #{picture}, document = #{document}, minCount = #{minCount}, maxCount = #{maxCount}, inTransitInCount = #{inTransitInCount}, inTransitOutCount = #{inTransitOutCount}, retailPurchases = #{retailPurchases}, consumption = #{consumption},
WHERE id = #{id}
