mapper.select:
SELECT

FROM task_assignment


    id = #{id}


    AND createdAt = #{createdAt}


    AND #{createdAtEnd} > createdAt


    AND createdAt >= #{createdAtStart}


    AND updatedAt = #{updatedAt}


    AND isEnable = #{isEnable}


    AND equipmentId = #{equipmentId}


    AND equipmentDescription = #{equipmentDescription}


    AND equipmentPosition = #{equipmentPosition}


    AND taskType = #{taskType}


    AND taskId = #{taskId}


    AND engineerId = #{engineerId}


    AND engineerName = #{engineerName}


    AND priority = #{priority}


    AND status = #{status}


    AND recordId = #{recordId}


    AND companyId = #{companyId}


    AND remark = #{remark}




    AND #{statusLessThan} > status


    AND status > #{statusGreaterThan}




ORDER BY createdAt ASC, updatedAt DESC

mapper.update:
UPDATE task_assignment
createdAt = #{createdAt}, updatedAt = #{updatedAt}, isEnable = #{isEnable}, equipmentId = #{equipmentId}, equipmentDescription = #{equipmentDescription}, equipmentPosition = #{equipmentPosition}, taskType = #{taskType}, taskId = #{taskId}, engineerId = #{engineerId}, engineerName = #{engineerName}, priority = #{priority}, status = #{status}, recordId = #{recordId}, companyId = #{companyId}, remark = #{remark},
WHERE id = #{id}

mapper.select:
SELECT
  ts.*, mt.name as taskName, mt.description as taskDescription, mt.orderType, mt.serveId, mt.serveType
FROM task_assignment ts

  join engineer e on e.id = ts.engineerId and e.isEnable = true and e.userId = #{userId}

join maintenance_task mt on ts.taskId = mt.id

    and mt.serveId = #{serveId}


    AND mt.serveType = #{serveType}


    AND mt.orderType = #{orderType}



    ts.id = #{id}


    AND ts.createdAt = #{createdAt}


    AND #{createdAtEnd} > ts.createdAt


    AND ts.createdAt >= #{createdAtStart}


    AND ts.updatedAt = #{updatedAt}


    AND ts.isEnable = #{isEnable}


    AND ts.equipmentId = #{equipmentId}


    AND ts.taskId = #{taskId}


    AND ts.engineerId = #{engineerId}


    AND ts.priority = #{priority}


    AND ts.status = #{status}


    AND ts.recordId = #{recordId}


ORDER BY ts.createdAt DESC, ts.updatedAt DESC
