mapper.select:
SELECT

FROM stock_log


      id = #{id}


      AND createdAt >= #{startTime}


      AND #{endTime} >= createdAt


      AND updatedAt = #{updatedAt}


      AND isEnable = #{isEnable}


      AND stockId = #{stockId}


      AND stockTypeId = #{stockTypeId}


      AND inTransitInCount = #{inTransitInCount}


      AND inTransitOutCount = #{inTransitOutCount}


      AND sumPurchaseCount = #{sumPurchaseCount}


      AND sumPlanConsumeCount = #{sumPlanConsumeCount}


      AND sumRealConsumeCount = #{sumRealConsumeCount}


      AND orderAvgCostDay = #{orderAvgCostDay}


      AND dayAvgCountCost = #{dayAvgCountCost}


      AND holdingCostRatio = #{holdingCostRatio}





ORDER BY createdAt ASC, updatedAt DESC

mapper.update:
UPDATE stock_log
createdAt = #{createdAt}, updatedAt = #{updatedAt}, isEnable = #{isEnable}, stockId = #{stockId}, stockTypeId = #{stockTypeId}, inTransitInCount = #{inTransitInCount}, inTransitOutCount = #{inTransitOutCount}, sumPurchaseCount = #{sumPurchaseCount}, sumPlanConsumeCount = #{sumPlanConsumeCount}, sumRealConsumeCount = #{sumRealConsumeCount}, orderAvgCostDay = #{orderAvgCostDay}, dayAvgCountCost = #{dayAvgCountCost}, holdingCostRatio = #{holdingCostRatio},
WHERE id = #{id}
