mapper.select:
SELECT

FROM supplier_card


      id = #{id}


      AND createdAt = #{createdAt}


      AND updatedAt = #{updatedAt}


      AND isEnable = #{isEnable}


      AND nameCn = #{nameCn}


      AND nameEn = #{nameEn}


      AND logo = #{logo}


      AND website = #{website}


      AND country = #{country}


      AND cityIndex1 = #{cityIndex1}


      AND cityIndex2 = #{cityIndex2}


      AND cityIndex3 = #{cityIndex3}


      AND cityIndex4 = #{cityIndex4}


      AND cityIndex5 = #{cityIndex5}


      AND addressPrefix = #{addressPrefix}


      AND addressDetail = #{addressDetail}


      AND postalCode = #{postalCode}


      AND hotline = #{hotline}


      AND lever = #{lever}


      AND isAuth = #{isAuth}


      AND isLock = #{isLock}


      AND status = #{status}


      AND contactJson = #{contactJson}


      AND slideshow = #{slideshow}


      AND businessScopeJson = #{businessScopeJson}


      AND descriptionInfo = #{descriptionInfo}


      AND serviceProcessInfo = #{serviceProcessInfo}


      AND qualificationInfo = #{qualificationInfo}


      AND configJson = #{configJson}


      AND currentId = #{currentId}


      AND applicationId = #{applicationId}


      AND creatorCompanyId = #{creatorCompanyId}


      AND creatorCompanyName = #{creatorCompanyName}





ORDER BY createdAt ASC, updatedAt DESC

mapper.update:
UPDATE supplier_card
createdAt = #{createdAt}, updatedAt = #{updatedAt}, isEnable = #{isEnable}, nameCn = #{nameCn}, nameEn = #{nameEn}, logo = #{logo}, website = #{website}, country = #{country}, cityIndex1 = #{cityIndex1}, cityIndex2 = #{cityIndex2}, cityIndex3 = #{cityIndex3}, cityIndex4 = #{cityIndex4}, cityIndex5 = #{cityIndex5}, addressPrefix = #{addressPrefix}, addressDetail = #{addressDetail}, postalCode = #{postalCode}, hotline = #{hotline}, lever = #{lever}, isAuth = #{isAuth}, isLock = #{isLock}, status = #{status}, contactJson = #{contactJson}, slideshow = #{slideshow}, businessScopeJson = #{businessScopeJson}, descriptionInfo = #{descriptionInfo}, serviceProcessInfo = #{serviceProcessInfo}, qualificationInfo = #{qualificationInfo}, configJson = #{configJson}, currentId = #{currentId}, applicationId = #{applicationId}, creatorCompanyId = #{creatorCompanyId}, creatorCompanyName = #{creatorCompanyName},
WHERE id = #{id}
